    <Flex
      w={250}
      h={200}
      borderColor='black.200'
      justifyContent="space-between"
      flexDir={"column"}
      borderRadius={10}
      p={5}
      shadow='md'
    >

      <Text fontSize={15} fontWeight={'bold'} color={'teal'}> {product.name} </Text>
      <Flex w='100%' justify='center'>
        <ContainerOptions
          title='Estoque'
          value={product.qty_stock}
        />
